mapper.select:
SELECT 

FROM spc_saleorder_entry a

WHERE a.id = #{id}

SELECT 

FROM spc_saleorder_entry a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.main_id = #{spcSaleorder.id}


		AND a.material_id = #{material.id}


		AND a.isgift = #{isgift}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM spc_saleorder_entry a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE spc_saleorder_entry SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE main_id = #{spcSaleorder.id}
